package Assignment2;

public class Ques2 {
	public static void printAsciiChar() {
        for (int i = 0; i <= 127; i++) {
            System.out.println(i + " -> " + (char) i);
        }
    }
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 printAsciiChar();

	}
}
