package Assignment2;

public class Ques9 {
	 public static int sumOfFirstTenNumbers() {
	        int sum = 0;
	        for (int i = 1; i <= 10; i++) {
	            sum += i;
	        }
	        return sum;
	    }
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		 System.out.println("Sum of first 10 natural numbers: " + sumOfFirstTenNumbers());

	}

}
